import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'
import router from '@/router'
const Cryptr = require('cryptr');
const cryptr = new Cryptr('9513RusskyHusky');

Vue.use(Vuex)

export default new Vuex.Store({
	state: {
    /* General Stuffs */
		serverURL: 'http://167.99.239.218:8081/hotelapp',
    loader: false,

    /* Template Stuffs */
    clipped: false,
    drawer: true,
    fixed: false,
    items: [
      { icon: 'fas fa-hotel', title: 'Rack', to: '/' },
      { icon: 'mdi-view-dashboard', title: 'Habitaciones', to: '/rooms' },
      { icon: 'mdi-account', title: 'Huéspedes', to: '/guests' },
      { icon: 'fas fa-check', title: 'Check-in', to: '/checkin' },
      { icon: 'mdi-clipboard-outline', title: 'Reservas', to: '/reservations' }
    ],
    miniVariant: false,
    right: true,
    rightDrawer: false,
    title: 'TingesoCreative',
    sidebarBackgroundColor: 'rgba(27, 27, 27, 0.74)',

    /* Guests Stuffs */
    guests: [],
    guest: {},
    guestId: '',
    storeGuestSuccess: false,
    updateGuestSuccess: false,
    deleteGuestSuccess: false,
    storeGuestError: false,
    updateGuestError: false,
    deleteGuestError: false,

    /* Reservations Stuffs */
    reservations: [],
    reservation: {},
    clientSelect: false,
    /* Reserved Room Stuffs */
    reservationId: '',
    reservedRoom: {},
    reservedRoomEnd: {},
    /* Room Stuffs */
    type: '',
    capacity: '',
    smoke: '',
    minPrice: '',
    maxPrice: '',
    dateIni: '',
    dateOut: '',
    number: '',
    status: '',
    rooms: [],
    room: '',
    roomId: '',
    filtredRooms: [],
    selectedRoom: '',
