    goAhead({ commit }) {
      commit('updateLoader', true);
      router.go(1);
      commit('updateLoader', false);
    },
    /* Alerts */
    cleanAlerts({ commit }) {
      commit('updateStoreGuestSuccess', false);
      commit('updateUpdateGuestSuccess', false);
      commit('updateDeleteGuestSuccess', false);
      commit('updateStoreGuestError', false);
      commit('updateUpdateGuestError', false);
      commit('updateDeleteGuestError', false);
      console.log('Las alertas han sido borradas con éxito');
    },
    /* Guests Stuffs */
    /* Guest.index */
    async getGuests({ commit, dispatch }) {
      try {
        commit('updateLoader', true);
        await axios
          .get(this.state.serverURL + '/guests/')
          .then(response => {
            const guests = response.data;
            if (guests.length != 0) {
              commit('updateLoader', false);
              this.state.guests = guests;
              dispatch('cleanAlerts');
            }
            else {
              console.log('No hay huéspedes alojándoce.')
            }
          })
          .catch(e => {
            console.log(e.response);
          })
      } catch (error) {
        console.log(error)
      }
    },
    /*  Guest.show */
    async getGuest({ commit }) {
      try {
        commit('updateLoader', true);
        await axios
          .get(this.state.serverURL + `/guests/${this.state.guestId}`)
          .then(response => {
            const guest = response.data;
            if (guest.length != 0) {
              commit('updateLoader', false);
              this.state.guest = {
                firstName: guest.firstName,
                lastName: guest.lastName,
                email: guest.email,
              }
            }
            else {
              console.log('No hay huéspedes o no se puede acceder a ellos');
